import React from 'react';

export default ({ items, clickFunc }) => {
    return (
        <div>
            {
                items && items.length ? items.map((itemsRow, i) =>
                    <div
                        key={i}
                        style={{ display: 'flex' }}
                    >
                        {
                            itemsRow.map((item, j) =>
                                <div
                                    key={j}
                                    onClick={ () => clickFunc(i, j) }
                                    style={{ width: '20px', height: '20px', border: '1px solid red' }}>
                                    { item }
                                </div>
                            )
                        }
                    </div>
                ) : ''
            }
        </div>
    )
}